import './console.js';
import process from './node/process.js';
import './utils2.js';
import { clearImmediate, clearInterval, clearTimeout, setImmediate, setInterval, setTimeout } from './timers.js';
import { TextEncoder } from './text-encoder.js';
import { Headers } from './headers.js';
import { fetch, Request, Response } from './fetch.js';
import { XMLHttpRequest } from './xhr.js';
import { Storage } from './Storage.js';
import { WebSocket } from './websocket.js';
import { crypto } from './crypto.js';

/* global globalThis */

// Browser globals
globalThis.clearImmediate = clearImmediate;
globalThis.clearInterval = clearInterval;
globalThis.clearTimeout = clearTimeout;
